INSERT INTO `tb_teleplay` VALUES (102, '最新:10集', '塔科马消防队第一季', '美国  ', 2019, 5, '喜剧', '102.jpg', '塔科马FD的最新负责人特里·麦康基（Terry McConky）正在接受市议会的审查，以认真对待其电台的收视率。 同时，特里（Terry）的工作人员参与进来，使他的工作陷入危险。');
